mapper.insert:
INSERT INTO
art_market_db.order(
order_id, product_id, member_id, product_name, total_amount, order_date, deadline, order_status
)
VALUES(
#{orderId}, #{productId}, #{memberId}, #{productName}, #{totalAmount}, sysdate(), #{deadline}, #{orderStatus}
)

mapper.select:
SELECT o.order_id, m.nickname, o.total_amount, o.order_date, o.deadline, o.order_status FROM art_market_db.order o INNER JOIN product p ON p.product_id = o.product_id INNER JOIN member m ON m.member_id = o.member_id where o.member_id = #{memberId} and o.order_status <> '결제 대기'
and p.category = #{category}
and o.order_status = #{orderStatus}
and m.nickname = #{nickname}
and o.order_date between #{startDate} and concat(#{endDate}, ' 23:59:59')
ORDER BY o.order_date DESC

SELECT o.order_id, m.nickname, o.total_amount, o.order_date, o.deadline, o.order_status FROM art_market_db.order o INNER JOIN product p ON p.product_id = o.product_id INNER JOIN member m ON m.member_id = p.member_id where m.member_id = #{memberId} and o.order_status <> '결제 대기'
and p.category = #{category}
and o.order_status = #{orderStatus}
and m.nickname = #{nickname}
and o.order_date between #{startDate} and concat(#{endDate}, ' 23:59:59')
ORDER BY o.order_date DESC

mapper.update:
UPDATE art_market_db.order
SET order_status = #{orderStatus}
where order_id = #{orderId}
